                        document.querySelector("#license-agreement-form").submit();
                    }
                } else {
                    var formId   = document.querySelector("#license-agreement-form").getAttribute('id'),
                    $form = document.getElementById(formId);
                    let data = new FormData($form);
                    fetch($form.getAttribute('action'), {
                        method: 'post',
                        body: new URLSearchParams(data).toString(),
                        headers: {
                            'Content-type': 'application/x-www-form-urlencoded'
                        },
                        cache: 'no-store',
                        credentials: 'include'
                    }).then(function (response) {
                        return response.text()
                    }).then(function (data) {
                        let $formContainer = document.querySelector('*[data-ajax-form-id="' + formId + '"]');
                        $formContainer.innerHTML = '';
                        loadData(data, '#' + $formContainer.id).then(() => {
                            window.document.dispatchEvent(loadAjaxFormEvent[formId]);
                        });
                    }).catch(function (error) {
                        console.log(error);
                    });
                }
            });
        }

        if (document.querySelector('#license-agreement-form.js-badger-accordion')) {
            form.fv.on('core.form.invalid', function() {
                let invalidClasses = ['.is-invalid', '.is-danger', '[aria-invalid="true"]', '.is-invalid-input', '.uk-form-danger'];
                firstInvalid = null;
                invalidClasses.forEach(ic => {
                    if (document.querySelector('#license-agreement-form ' + ic) !== null) {
                        firstInvalid = document.querySelector('#license-agreement-form ' + ic);
                    }
                });
                let fieldset = firstInvalid.closest('fieldset');
                let fieldsetIndex = fieldset.dataset.acIndex;
                phpfbAccordion["#license-agreement-form"].closeAll();
                phpfbAccordion["#license-agreement-form"].open(fieldsetIndex);
            });
        }

        if (dataAttr.fvNoIcon !== undefined) {
            form.fv.deregisterPlugin('icon');
        }

        if (!document.querySelector("#license-agreement-form").classList.contains('bulma-form') || !document.querySelector("#license-agreement-form").classList.contains('bulma-form-horizontal')) {
            form.fv.deregisterPlugin('message');
        }

        if (document.querySelector('#' + formId + ' button[type="reset"]')) {
